"use strict";(self.webpackChunkinterslavic_fun=self.webpackChunkinterslavic_fun||[]).push([[9903],{8112:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"learnSidebar":[{"type":"category","label":"Uvod","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Kratka povijest me\u0111uslav","href":"/hr/learn/introduction/history","docId":"learn/introduction/history","unlisted":false},{"type":"link","label":"Kriterij dizajna","href":"/hr/learn/introduction/design-criteria","docId":"learn/introduction/design-criteria","unlisted":false},{"type":"link","label":"Konstruirani slavenski jezici","href":"/hr/learn/introduction/constructed-slavic-languages","docId":"learn/introduction/constructed-slavic-languages","unlisted":false},{"type":"link","label":"Usporedba jezika","href":"/hr/learn/introduction/language-comparison","docId":"learn/introduction/language-comparison","unlisted":false}],"href":"/hr/learn/introduction/"},{"type":"link","label":"Pravopis","href":"/hr/learn/orthography","docId":"learn/orthography","unlisted":false},{"type":"link","label":"Fonologija","href":"/hr/learn/phonology","docId":"learn/phonology","unlisted":false},{"type":"link","label":"Izgovor","href":"/hr/learn/pronunciation","docId":"learn/pronunciation","unlisted":false},{"type":"category","label":"Gramatika","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Imenice","href":"/hr/learn/grammar/nouns","docId":"learn/grammar/nouns","unlisted":false},{"type":"link","label":"Pridjevi","href":"/hr/learn/grammar/adjectives","docId":"learn/grammar/adjectives","unlisted":false},{"type":"link","label":"Zamjenice","href":"/hr/learn/grammar/pronouns","docId":"learn/grammar/pronouns","unlisted":false},{"type":"link","label":"Brojevi","href":"/hr/learn/grammar/numerals","docId":"learn/grammar/numerals","unlisted":false},{"type":"link","label":"Glagoli","href":"/hr/learn/grammar/verbs","docId":"learn/grammar/verbs","unlisted":false},{"type":"link","label":"Prijedlog","href":"/hr/learn/grammar/prepositions","docId":"learn/grammar/prepositions","unlisted":false},{"type":"link","label":"Veznici","href":"/hr/learn/grammar/conjunctions","docId":"learn/grammar/conjunctions","unlisted":false},{"type":"link","label":"Sintaksa","href":"/hr/learn/grammar/syntax","docId":"learn/grammar/syntax","unlisted":false}],"href":"/hr/learn/grammar/"},{"type":"category","label":"Vocabulary","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Formacija rije\u010di","href":"/hr/learn/vocabulary/word-formation","docId":"learn/vocabulary/word-formation","unlisted":false},{"type":"link","label":"Derivacija rije\u010di","href":"/hr/learn/vocabulary/derivation","docId":"learn/vocabulary/derivation","unlisted":false},{"type":"link","label":"Aromatizacija","href":"/hr/learn/vocabulary/flavourisation","docId":"learn/vocabulary/flavourisation","unlisted":false}]},{"type":"category","label":"Jednostavna gramatika","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Razina 1","href":"/hr/learn/simple-grammar/level-1","docId":"learn/simple-grammar/level-1","unlisted":false},{"type":"link","label":"Razina 2","href":"/hr/learn/simple-grammar/level-2","docId":"learn/simple-grammar/level-2","unlisted":false}],"href":"/hr/learn/simple-grammar/"},{"type":"category","label":"Miscellaneous","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Panslavenska \u0161tafeta","href":"/hr/learn/misc/pan-slavic-relay","docId":"learn/misc/pan-slavic-relay","unlisted":false},{"type":"link","label":"Personal pronouns in the Slavic languages","href":"/hr/learn/misc/personal-pronouns","docId":"learn/misc/personal-pronouns","unlisted":false},{"type":"link","label":"Brojevi 1-10 u slavenskim jezicima","href":"/hr/learn/misc/numbers-1-10","docId":"learn/misc/numbers-1-10","unlisted":false}]},{"type":"link","label":"\u010cesto postavljana pitanja (FAQ)","href":"/hr/learn/faq","docId":"learn/faq","unlisted":false}],"resourcesSidebar":[{"type":"category","label":"Applets","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Intelligibility Builder","href":"/hr/resources/applets/intelligibility","docId":"resources/applets/intelligibility","unlisted":false},{"type":"link","label":"Transliterator","href":"/hr/resources/applets/transliterator","docId":"resources/applets/transliterator","unlisted":false}],"href":"/hr/resources/applets/"},{"type":"link","label":"Resursi","href":"/hr/resources/","docId":"resources/index","unlisted":false},{"type":"link","label":"Tipkovnice","href":"/hr/resources/keyboards","docId":"resources/keyboards","unlisted":false}]},"docs":{"learn/faq":{"id":"learn/faq","title":"\u010cesto postavljana pitanja (FAQ)","description":"\u0160to je me\u0111uslavenski (Med\u017euslovjansky)? \\\\","sidebar":"learnSidebar"},"learn/grammar/adjectives":{"id":"learn/grammar/adjectives","title":"Pridjevi","description":"Infleksija","sidebar":"learnSidebar"},"learn/grammar/conjunctions":{"id":"learn/grammar/conjunctions","title":"Veznici","description":"Ve\u0107ina prijedloga u Slovianskom upravlja genitivom. To posebno vrijedi za sve prijedloge koji su izvedeni od imenica, pridjeva ili priloga. U slu\u010daju prijedloga koji se odnose na mjesto i vrijeme, sljede\u0107e se mo\u017ee uzeti kao smjernica, iako postoje iznimke:","sidebar":"learnSidebar"},"learn/grammar/index":{"id":"learn/grammar/index","title":"Gramatika","description":"Me\u0111uslavenski je za Slavene prirodan i vrlo \u0161iroko razumljiv jezik. U potpunosti se temelji na oblicima zajedni\u010dkim slavenskim jezicima, a kad zajedni\u010dkih oblika nema, na ve\u0107inskim rje\u0161enjima. Ima gramati\u010dki rod, \u0161est pade\u017ea plus izborni vokativ, verbalni aspekt i punu konjugaciju \u2013 stvari s kojima je gotovo svaki Slaven upoznat. Osim toga, sadr\u017ei i neke materijale kojih nema u ve\u0107ini slavenskih jezika, ali \u010dije poznavanje mo\u017ee biti od velike pomo\u0107i u razumijevanju jezika koji ga koriste.","sidebar":"learnSidebar"},"learn/grammar/nouns":{"id":"learn/grammar/nouns","title":"Imenice","description":"Genders, numbers, cases, declensions and more","sidebar":"learnSidebar"},"learn/grammar/numerals":{"id":"learn/grammar/numerals","title":"Brojevi","description":"Kardinalni brojevi \\\\","sidebar":"learnSidebar"},"learn/grammar/prepositions":{"id":"learn/grammar/prepositions","title":"Prijedlog","description":"Veznici \\\\","sidebar":"learnSidebar"},"learn/grammar/pronouns":{"id":"learn/grammar/pronouns","title":"Zamjenice","description":"Istra\u017eite kori\u0161tenje, deklinaciju i primjere zamjenica u me\u0111uslavenskom jeziku. Prou\u010dite osobne, povratne, posvojne, pokazne, odnosne, upitne i neodre\u0111ene zamjenice.","sidebar":"learnSidebar"},"learn/grammar/syntax":{"id":"learn/grammar/syntax","title":"Sintaksa","description":"Red rije\u010di je u osnovi slobodan, ali preferirani (i stilski najneutralniji) red rije\u010di je subjekt \u2013 glagol \u2013 objekt (SVO). Nije obavezno, ali imajte na umu da postavljanje objekta ispred subjekta ne\u0107e pomo\u0107i u razja\u0161njavanju zna\u010denja. To nije slu\u010daj s re\u010denicama u kojima je osobna zamjenica ili zamjenicaisv[kto] imaju svoje oblike akuzativa, pa je zna\u010denje uvijek jasno.","sidebar":"learnSidebar"},"learn/grammar/verbs":{"id":"learn/grammar/verbs","title":"Glagoli","description":"Svr\u0161eni i nesvr\u0161eni vid \\\\","sidebar":"learnSidebar"},"learn/introduction/constructed-slavic-languages":{"id":"learn/introduction/constructed-slavic-languages","title":"Konstruirani slavenski jezici","description":"Kao i mnoge druge velike jezi\u010dne obitelji, slavenski su jezici nadahnuli tvorce jezika da izgrade umjetni slavenski jezik. Ili samo radi zabave (hipotetski sjevernoslavenski jezik, na primjer), ili sa svrhom stvaranja jezika razumljivog govornicima razli\u010ditih slavenskih jezika. Tijekom povijesti bilo je mnogo poku\u0161aja slavenskog me\u0111ujezika. Ponekad ta nastojanja imaju politi\u010dku (panslavensku) pozadinu, ponekad se jednostavno temelje na pretpostavci da su slavenski jezici dovoljno sli\u010dni jedni drugima da bi takav jezik uop\u0107e bio mogu\u0107. Ovdje slijedi popis pomo\u0107nih slavenskih jezika, nastalih tijekom godina.","sidebar":"learnSidebar"},"learn/introduction/design-criteria":{"id":"learn/introduction/design-criteria","title":"Kriterij dizajna","description":"Me\u0111uslavenski se temelji na zajedni\u010dkom naslije\u0111u svih slavenskih naroda, a razlike se premo\u0161\u0107uju gra\u0111enjem kompromisa. Svi se slavenski jezici tretiraju jednako, iako se ne mo\u017ee izbje\u0107i da je slavenski me\u0111ujezik bli\u017ei jezicima \u201esrednje razine\u201c poput slova\u010dkog ili rusinskog nego perifernim jezicima poput lu\u017ei\u010dkosrpskog ili makedonskog. Me\u0111uslavenski jezik izri\u010dito nije zami\u0161ljen kao \u201eslavenskiji od Slavena\u201c pro\u010di\u0161\u0107avanjem od stranih utjecaja, iako \u0107e jezik koji se temelji na sli\u010dnostima slavenskih jezika ionako automatski eliminirati mnoge lokalne (neslavenske) utjecaje. Nema, me\u0111utim, razloga za isklju\u010divanje internacionalnog rje\u010dnika, pod uvjetom da ga mogu prepoznati svi, ili barem ve\u0107ina, slavenskih govornika.","sidebar":"learnSidebar"},"learn/introduction/history":{"id":"learn/introduction/history","title":"Kratka povijest me\u0111uslav","description":"Povijest me\u0111uslavenskog jezika usko je povezana s panslavizmom, ideologijom koja smatra da su Slaveni jedan narod i ima za cilj njihovo kulturno i politi\u010dko ujedinjenje. Uz ideju zajedni\u010dke slavenske dr\u017eave dolazi i ideja zajedni\u010dkog slavenskog jezika. Od po\u010detka se postavljalo pitanje kako bi taj jezik trebao izgledati.","sidebar":"learnSidebar"},"learn/introduction/index":{"id":"learn/introduction/index","title":"Uvod","description":"\u0160to je me\u0111uslavenski? \\\\","sidebar":"learnSidebar"},"learn/introduction/language-comparison":{"id":"learn/introduction/language-comparison","title":"Usporedba jezika","description":"Saznajte o me\u0111uslavenskom jeziku i kako se on uspore\u0111uje s drugim jezicima, uklju\u010duju\u0107i engleski, ruski, esperanto, slovio i praslavenski/staroslavenski.","sidebar":"learnSidebar"},"learn/misc/numbers-1-10":{"id":"learn/misc/numbers-1-10","title":"Brojevi 1-10 u slavenskim jezicima","description":"Prirodni slavenski jezici","sidebar":"learnSidebar"},"learn/misc/pan-slavic-relay":{"id":"learn/misc/pan-slavic-relay","title":"Panslavenska \u0161tafeta","description":"Panslavenska \u0161tafeta odr\u017eana je u rujnu-listopadu 2009. godine.","sidebar":"learnSidebar"},"learn/misc/personal-pronouns":{"id":"learn/misc/personal-pronouns","title":"Personal pronouns in the Slavic languages","description":"Explore the personal pronouns in the Slavic languages, including natural, constructed, and fictional dialects, for a comprehensive linguistic comparison.","sidebar":"learnSidebar"},"learn/orthography":{"id":"learn/orthography","title":"Pravopis","description":"Standardna abeceda \\\\","sidebar":"learnSidebar"},"learn/phonology":{"id":"learn/phonology","title":"Fonologija","description":"Inventar fonema \\\\","sidebar":"learnSidebar"},"learn/pronunciation":{"id":"learn/pronunciation","title":"Izgovor","description":"Pregled \\\\","sidebar":"learnSidebar"},"learn/simple-grammar/index":{"id":"learn/simple-grammar/index","title":"Jednostavna gramatika","description":"Pojednostavljena gramatika, ili :notr[Slovianto], vrlo je pojednostavljeni oblik me\u0111uslavenskog koji omogu\u0107uje neslavenskim ljudima da se izraze na vrlo osnovnoj razini kada posje\u0107uju slavenske zemlje ili odr\u017eavaju kontakt sa Slavenima putem interneta. Gramatika je ograni\u010dena na minimum, a oblici koji nisu nu\u017eno potrebni uop\u0107e se ne koriste. Budu\u0107i da ne sadr\u017ei ni\u0161ta \u0161to se ne mo\u017ee na\u0107i na slavenskim jezicima, rezultat ne\u0107e izgledati ili zvu\u010dati previ\u0161e nezgrapno govornicima slavenskog jezika, ve\u0107 \u0107e ih podsje\u0107ati na jezik kojim se slu\u017ee mala djeca.","sidebar":"learnSidebar"},"learn/simple-grammar/level-1":{"id":"learn/simple-grammar/level-1","title":"Razina 1","description":"Abeceda i izgovor","sidebar":"learnSidebar"},"learn/simple-grammar/level-2":{"id":"learn/simple-grammar/level-2","title":"Razina 2","description":"Gender, nouns, adjectives, verbs","sidebar":"learnSidebar"},"learn/vocabulary/derivation":{"id":"learn/vocabulary/derivation","title":"Derivacija rije\u010di","description":"Praslavenski \\\\","sidebar":"learnSidebar"},"learn/vocabulary/flavourisation":{"id":"learn/vocabulary/flavourisation","title":"Aromatizacija","description":"Me\u0111uslavenski dijalekti \\\\","sidebar":"learnSidebar"},"learn/vocabulary/word-formation":{"id":"learn/vocabulary/word-formation","title":"Formacija rije\u010di","description":"Sufiksi \\\\","sidebar":"learnSidebar"},"resources/applets/index":{"id":"resources/applets/index","title":"Applets","description":"","sidebar":"resourcesSidebar"},"resources/applets/intelligibility":{"id":"resources/applets/intelligibility","title":"Intelligibility Builder","description":"Construct a special notation to indicate the intelligibility of a given form in the Slavic languages. Drag and drop a synset XML file to the table below to get started (see an example). After you finish with the table, hover over the code and click the \\"Copy\\" button to copy the code to your clipboard.","sidebar":"resourcesSidebar"},"resources/applets/transliterator":{"id":"resources/applets/transliterator","title":"Transliterator","description":"Converts text from one writing system to another.","sidebar":"resourcesSidebar"},"resources/index":{"id":"resources/index","title":"Resursi","description":"Dobro do\u0161li u na\u0161 pa\u017eljivo pripremljeni vodi\u010d posve\u0107en svijetu me\u0111uslavenskih jezika. Imamo niz resursa koji \u0107e vam olak\u0161ati putovanje u\u010denjem, od mre\u017enih zajednica i obrazovnog sadr\u017eaja do softverskih alata i literature. Krenimo zajedno u ovu zadivljuju\u0107u lingvisti\u010dku avanturu.","sidebar":"resourcesSidebar"},"resources/keyboards":{"id":"resources/keyboards","title":"Tipkovnice","description":"Windows \\\\","sidebar":"resourcesSidebar"}}}}')}}]);