"use strict";(globalThis.webpackChunkinterslavic_fun=globalThis.webpackChunkinterslavic_fun||[]).push([[6162],{8035:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"learnSidebar":[{"type":"link","href":"/pl/learn/introduction/","label":"Wst\u0119p","docId":"learn/introduction/index","unlisted":false},{"type":"link","href":"/pl/learn/orthography","label":"Ortografia","docId":"learn/orthography","unlisted":false},{"type":"link","href":"/pl/learn/phonology","label":"Fonologia","docId":"learn/phonology","unlisted":false},{"type":"link","href":"/pl/learn/pronunciation","label":"Wymowa","docId":"learn/pronunciation","unlisted":false},{"type":"category","label":"Gramatyka","collapsible":true,"collapsed":true,"items":[{"type":"link","href":"/pl/learn/grammar/nouns","label":"Rzeczowniki","docId":"learn/grammar/nouns","unlisted":false},{"type":"link","href":"/pl/learn/grammar/adjectives","label":"Przymiotniki","docId":"learn/grammar/adjectives","unlisted":false},{"type":"link","href":"/pl/learn/grammar/pronouns","label":"Zaimki","docId":"learn/grammar/pronouns","unlisted":false},{"type":"link","href":"/pl/learn/grammar/numerals","label":"Cyfry","docId":"learn/grammar/numerals","unlisted":false},{"type":"link","href":"/pl/learn/grammar/verbs","label":"Czasowniki","docId":"learn/grammar/verbs","unlisted":false},{"type":"link","href":"/pl/learn/grammar/prepositions","label":"Przyimki","docId":"learn/grammar/prepositions","unlisted":false},{"type":"link","href":"/pl/learn/grammar/conjunctions","label":"Sp\xf3jniki","docId":"learn/grammar/conjunctions","unlisted":false},{"type":"link","href":"/pl/learn/grammar/syntax","label":"Sk\u0142adnia","docId":"learn/grammar/syntax","unlisted":false}],"href":"/pl/learn/grammar/"},{"type":"category","label":"Vocabulary","collapsible":true,"collapsed":true,"items":[{"type":"link","href":"/pl/learn/vocabulary/word-formation","label":"Tworzenie s\u0142\xf3w","docId":"learn/vocabulary/word-formation","unlisted":false},{"type":"link","href":"/pl/learn/vocabulary/derivation","label":"Pochodzenie s\u0142\xf3w","docId":"learn/vocabulary/derivation","unlisted":false},{"type":"link","href":"/pl/learn/vocabulary/flavourisation","label":"Aromatyzowanie","docId":"learn/vocabulary/flavourisation","unlisted":false}]},{"type":"category","label":"Prosta gramatyka","collapsible":true,"collapsed":true,"items":[{"type":"link","href":"/pl/learn/simple-grammar/level-1","label":"Poziom 1","docId":"learn/simple-grammar/level-1","unlisted":false},{"type":"link","href":"/pl/learn/simple-grammar/level-2","label":"Poziom 2","docId":"learn/simple-grammar/level-2","unlisted":false}],"href":"/pl/learn/simple-grammar/"},{"type":"category","label":"R\xf3\u017cne","collapsible":true,"collapsed":true,"items":[{"type":"link","href":"/pl/learn/misc/history","label":"Kr\xf3tka historia mi\u0119dzys\u0142owia\u0144ska","docId":"learn/misc/history","unlisted":false},{"type":"link","href":"/pl/learn/misc/design-criteria","label":"Kryteria projektowe","docId":"learn/misc/design-criteria","unlisted":false},{"type":"link","href":"/pl/learn/misc/constructed-slavic-languages","label":"Skonstruowane j\u0119zyki s\u0142owia\u0144skie","docId":"learn/misc/constructed-slavic-languages","unlisted":false},{"type":"link","href":"/pl/learn/misc/language-comparison","label":"Por\xf3wnanie j\u0119zyk\xf3w","docId":"learn/misc/language-comparison","unlisted":false},{"type":"link","href":"/pl/learn/misc/pan-slavic-relay","label":"Przeka\u017anik pans\u0142owia\u0144ski","docId":"learn/misc/pan-slavic-relay","unlisted":false},{"type":"link","href":"/pl/learn/misc/personal-pronouns","label":"Personal pronouns in the Slavic languages","docId":"learn/misc/personal-pronouns","unlisted":false},{"type":"link","href":"/pl/learn/misc/numbers-1-10","label":"Numery od 1 do 10 w j\u0119zykach s\u0142owia\u0144skich","docId":"learn/misc/numbers-1-10","unlisted":false}]},{"type":"link","href":"/pl/learn/faq","label":"Cz\u0119sto zadawane pytania (FAQ)","docId":"learn/faq","unlisted":false}],"resourcesSidebar":[{"type":"category","label":"Applets","collapsible":true,"collapsed":true,"items":[{"type":"link","href":"/pl/resources/applets/intelligibility","label":"Intelligibility Builder","docId":"resources/applets/intelligibility","unlisted":false},{"type":"link","href":"/pl/resources/applets/transliterator","label":"Transliterator","docId":"resources/applets/transliterator","unlisted":false}],"href":"/pl/resources/applets/"},{"type":"link","href":"/pl/resources/","label":"Zasoby","docId":"resources/index","unlisted":false},{"type":"link","href":"/pl/resources/keyboards","label":"Klawiatury","docId":"resources/keyboards","unlisted":false}]},"docs":{"learn/faq":{"id":"learn/faq","title":"Cz\u0119sto zadawane pytania (FAQ)","description":"Dowiedz si\u0119, co to jest j\u0119zyk mi\u0119dzys\u0142owia\u0144ski, jak go nauczy\u0107 i jak go u\u017cywa\u0107, a tak\u017ce inne cz\u0119sto zadawane pytania.","sidebar":"learnSidebar"},"learn/grammar/adjectives":{"id":"learn/grammar/adjectives","title":"Przymiotniki","description":"Fleksja","sidebar":"learnSidebar"},"learn/grammar/conjunctions":{"id":"learn/grammar/conjunctions","title":"Sp\xf3jniki","description":"Wi\u0119kszo\u015b\u0107 przyimk\xf3w w S\u0142owia\u0144sku rz\u0105dzi dope\u0142niaczem. Jest to szczeg\xf3lnie prawdziwe w przypadku wszystkich przyimk\xf3w, kt\xf3re powsta\u0142y od rzeczownik\xf3w, przymiotnik\xf3w lub przys\u0142\xf3wk\xf3w. W przypadku przyimk\xf3w odnosz\u0105cych si\u0119 do miejsca i czasu jako wskaz\xf3wk\u0119 mo\u017cna przyj\u0105\u0107, cho\u0107 s\u0105 wyj\u0105tki:","sidebar":"learnSidebar"},"learn/grammar/index":{"id":"learn/grammar/index","title":"Gramatyka","description":"Mi\u0119dzys\u0142owia\u0144ski jest j\u0119zykiem naturalistycznym i bardzo dobrze zrozumia\u0142ym dla S\u0142owian. W ca\u0142o\u015bci opiera si\u0119 na formach wsp\xf3lnych dla j\u0119zyk\xf3w s\u0142owia\u0144skich, a w przypadku braku form wsp\xf3lnych na rozwi\u0105zaniach wi\u0119kszo\u015bciowych. Ma rodzaj gramatyczny, sze\u015b\u0107 przypadk\xf3w plus opcjonalny wo\u0142acz, aspekt werbalny i pe\u0142n\u0105 koniugacj\u0119 \u2013 rzeczy, kt\xf3re zna praktycznie ka\u017cdy S\u0142owianin. Ponadto zawiera r\xf3wnie\u017c struktury, kt\xf3rych nie ma w wi\u0119kszo\u015bci j\u0119zyk\xf3w s\u0142owia\u0144skich, ale kt\xf3rych znajomo\u015b\u0107 mo\u017ce by\u0107 bardzo pomocna w zrozumieniu j\u0119zyk\xf3w, kt\xf3re ich u\u017cywaj\u0105.","sidebar":"learnSidebar"},"learn/grammar/nouns":{"id":"learn/grammar/nouns","title":"Rzeczowniki","description":"Deklinacja rzeczownik\xf3w w j\u0119zyku mi\u0119dzys\u0142owia\u0144skim, z przyk\u0142adami i wyja\u015bnieniami.","sidebar":"learnSidebar"},"learn/grammar/numerals":{"id":"learn/grammar/numerals","title":"Cyfry","description":"Liczby g\u0142\xf3wne \\\\","sidebar":"learnSidebar"},"learn/grammar/prepositions":{"id":"learn/grammar/prepositions","title":"Przyimki","description":"Sp\xf3jniki \\\\","sidebar":"learnSidebar"},"learn/grammar/pronouns":{"id":"learn/grammar/pronouns","title":"Zaimki","description":"Zbadaj u\u017cycie, odmian\u0119 i przyk\u0142ady zaimk\xf3w w j\u0119zyku mi\u0119dzys\u0142owia\u0144skim. Zapoznaj si\u0119 z zaimkami osobowymi, zwrotnymi, dzier\u017cawczymi, wskazuj\u0105cymi, wzgl\u0119dnymi, pytaj\u0105cymi i nieokre\u015blonymi.","sidebar":"learnSidebar"},"learn/grammar/syntax":{"id":"learn/grammar/syntax","title":"Sk\u0142adnia","description":"Kolejno\u015b\u0107 s\u0142\xf3w jest w zasadzie dowolna, ale preferowana (i najbardziej neutralna stylistycznie) kolejno\u015b\u0107 s\u0142\xf3w to podmiot \u2013 czasownik \u2013 dope\u0142nienie (SVO). Nie jest to obowi\u0105zkowe, ale nale\u017cy pami\u0119ta\u0107, \u017ce umieszczenie dope\u0142nienia przed podmiotem nie pomo\u017ce w wyja\u015bnieniu znaczenia. Nie dotyczy to zda\u0144, w kt\xf3rych zaimek osobowy lub zaimekisv[kto] maj\u0105 swoje w\u0142asne formy biernika, wi\u0119c znaczenie jest zawsze jasne.","sidebar":"learnSidebar"},"learn/grammar/verbs":{"id":"learn/grammar/verbs","title":"Czasowniki","description":"Aspekt dokonany i niedoskona\u0142y \\\\","sidebar":"learnSidebar"},"learn/introduction/index":{"id":"learn/introduction/index","title":"Wst\u0119p","description":"Co to jest mi\u0119dzys\u0142owia\u0144ski? \\\\","sidebar":"learnSidebar"},"learn/misc/constructed-slavic-languages":{"id":"learn/misc/constructed-slavic-languages","title":"Skonstruowane j\u0119zyki s\u0142owia\u0144skie","description":"Podobnie jak wiele innych du\u017cych rodzin j\u0119zykowych, j\u0119zyki s\u0142owia\u0144skie zainspirowa\u0142y tw\xf3rc\xf3w j\u0119zyk\xf3w do zbudowania sztucznego j\u0119zyka s\u0142owia\u0144skiego. Albo dla zabawy (na przyk\u0142ad hipotetyczny j\u0119zyk p\xf3\u0142nocnos\u0142owia\u0144ski), albo w celu stworzenia j\u0119zyka zrozumia\u0142ego dla u\u017cytkownik\xf3w r\xf3\u017cnych j\u0119zyk\xf3w s\u0142owia\u0144skich. Na przestrzeni dziej\xf3w podejmowano wiele pr\xf3b stworzenia j\u0119zyka s\u0142owia\u0144skiego. Czasem te starania maj\u0105 pod\u0142o\u017ce polityczne (pans\u0142owia\u0144skie), czasem opieraj\u0105 si\u0119 po prostu na za\u0142o\u017ceniu, \u017ce j\u0119zyki s\u0142owia\u0144skie s\u0105 do siebie na tyle podobne, \u017ce taki j\u0119zyk w og\xf3le jest mo\u017cliwy. Oto lista pomocniczych j\u0119zyk\xf3w s\u0142owia\u0144skich, tworzonych przez lata.","sidebar":"learnSidebar"},"learn/misc/design-criteria":{"id":"learn/misc/design-criteria","title":"Kryteria projektowe","description":"Mi\u0119dzys\u0142owia\u0144ska opiera si\u0119 na wsp\xf3lnym dziedzictwie wszystkich narod\xf3w s\u0142owia\u0144skich, a r\xf3\u017cnice niweluje si\u0119 buduj\u0105c kompromisy. Wszystkie j\u0119zyki s\u0142owia\u0144skie s\u0105 traktowane jednakowo, cho\u0107 nie da si\u0119 ukry\u0107, \u017ce s\u0142owia\u0144ski j\u0119zyk po\u015bredni jest bli\u017cszy j\u0119zykom \u201e\u015brodkowym\u201d, takim jak s\u0142owacki czy rusi\u0144ski, ni\u017c j\u0119zykom peryferyjnym, takim jak \u0142u\u017cycki czy macedo\u0144ski. Mi\u0119dzys\u0142owia\u0144ski wyra\u017anie nie ma by\u0107 \u201ebardziej s\u0142owia\u0144ski ni\u017c S\u0142owianie\u201d poprzez oczyszczenie go z obcych wp\u0142yw\xf3w, mimo \u017ce j\u0119zyk oparty na podobie\u0144stwach j\u0119zyk\xf3w s\u0142owia\u0144skich i tak automatycznie wyeliminuje wiele lokalnych (nies\u0142owia\u0144skich) wp\u0142yw\xf3w. Nie ma jednak powodu wyklucza\u0107 s\u0142ownictwa mi\u0119dzynarodowego, pod warunkiem, \u017ce jest ono rozpoznawane przez wszystkich lub przynajmniej wi\u0119kszo\u015b\u0107 u\u017cytkownik\xf3w j\u0119zyka s\u0142owia\u0144skiego.","sidebar":"learnSidebar"},"learn/misc/history":{"id":"learn/misc/history","title":"Kr\xf3tka historia mi\u0119dzys\u0142owia\u0144ska","description":"Historia j\u0119zyka mi\u0119dzys\u0142owia\u0144skiego jest \u015bci\u015ble zwi\u0105zana z panslawizmem, ideologi\u0105 uznaj\u0105c\u0105 S\u0142owian za jeden nar\xf3d i d\u0105\u017c\u0105c\u0105 do ich kulturowego i politycznego zjednoczenia. Wraz z ide\u0105 wsp\xf3lnego pa\u0144stwa s\u0142owia\u0144skiego pojawia si\u0119 tak\u017ce idea wsp\xf3lnego j\u0119zyka s\u0142owia\u0144skiego. Od pocz\u0105tku zastanawiano si\u0119, jak ten j\u0119zyk powinien wygl\u0105da\u0107.","sidebar":"learnSidebar"},"learn/misc/language-comparison":{"id":"learn/misc/language-comparison","title":"Por\xf3wnanie j\u0119zyk\xf3w","description":"Dowiedz si\u0119 o j\u0119zyku mi\u0119dzys\u0142owia\u0144skim i jak si\u0119 on por\xf3wnuje z innymi j\u0119zykami, w tym angielskim, rosyjskim, esperanto, s\u0142owio i pras\u0142owia\u0144skim/staro-cerkiewno-s\u0142owia\u0144skim.","sidebar":"learnSidebar"},"learn/misc/numbers-1-10":{"id":"learn/misc/numbers-1-10","title":"Numery od 1 do 10 w j\u0119zykach s\u0142owia\u0144skich","description":"Naturalne j\u0119zyki s\u0142owia\u0144skie","sidebar":"learnSidebar"},"learn/misc/pan-slavic-relay":{"id":"learn/misc/pan-slavic-relay","title":"Przeka\u017anik pans\u0142owia\u0144ski","description":"Sztafeta Pans\u0142owia\u0144ska odby\u0142a si\u0119 we wrze\u015bniu-pa\u017adzierniku 2009 roku.","sidebar":"learnSidebar"},"learn/misc/personal-pronouns":{"id":"learn/misc/personal-pronouns","title":"Personal pronouns in the Slavic languages","description":"Explore the personal pronouns in the Slavic languages, including natural, constructed, and fictional dialects, for a comprehensive linguistic comparison.","sidebar":"learnSidebar"},"learn/orthography":{"id":"learn/orthography","title":"Ortografia","description":"Standardowy alfabet \\\\","sidebar":"learnSidebar"},"learn/phonology":{"id":"learn/phonology","title":"Fonologia","description":"Spis fonem\xf3w \\\\","sidebar":"learnSidebar"},"learn/pronunciation":{"id":"learn/pronunciation","title":"Wymowa","description":"Wymowa w j\u0119zyku mi\u0119dzys\u0142owia\u0144skim, z uwzgl\u0119dnieniem r\xf3\u017cnic mi\u0119dzy j\u0119zykami s\u0142owia\u0144skimi.","sidebar":"learnSidebar"},"learn/simple-grammar/index":{"id":"learn/simple-grammar/index","title":"Prosta gramatyka","description":"Gramatyka uproszczona, czyli :notr[Slovianto], to wysoce uproszczona forma j\u0119zyka mi\u0119dzys\u0142owia\u0144skiego, kt\xf3ra pozwala osobom nies\u0142owia\u0144skim wyra\u017ca\u0107 si\u0119 na bardzo podstawowym poziomie podczas odwiedzania kraj\xf3w s\u0142owia\u0144skich lub utrzymywania kontaktu ze S\u0142owianami za po\u015brednictwem Internetu. Gramatyka ograniczona jest do absolutnego minimum, a formy, kt\xf3re nie s\u0105 absolutnie niezb\u0119dne, w og\xf3le nie s\u0105 u\u017cywane. Poniewa\u017c nie zawiera niczego, czego nie mo\u017cna znale\u017a\u0107 w j\u0119zykach s\u0142owia\u0144skich, wynik nie b\u0119dzie wygl\u0105da\u0142 ani brzmia\u0142 zbyt niezr\u0119cznie dla os\xf3b m\xf3wi\u0105cych po s\u0142owia\u0144sku, ale raczej przypomni im rodzaj j\u0119zyka, kt\xf3rym pos\u0142uguj\u0105 si\u0119 ma\u0142e dzieci.","sidebar":"learnSidebar"},"learn/simple-grammar/level-1":{"id":"learn/simple-grammar/level-1","title":"Poziom 1","description":"Alfabet i wymowa","sidebar":"learnSidebar"},"learn/simple-grammar/level-2":{"id":"learn/simple-grammar/level-2","title":"Poziom 2","description":"Gender, nouns, adjectives, verbs","sidebar":"learnSidebar"},"learn/vocabulary/derivation":{"id":"learn/vocabulary/derivation","title":"Pochodzenie s\u0142\xf3w","description":"Pras\u0142owia\u0144ski \\\\","sidebar":"learnSidebar"},"learn/vocabulary/flavourisation":{"id":"learn/vocabulary/flavourisation","title":"Aromatyzowanie","description":"Dialekty mi\u0119dzys\u0142owia\u0144skie \\\\","sidebar":"learnSidebar"},"learn/vocabulary/word-formation":{"id":"learn/vocabulary/word-formation","title":"Tworzenie s\u0142\xf3w","description":"Sufiksy \\\\","sidebar":"learnSidebar"},"resources/applets/index":{"id":"resources/applets/index","title":"Applets","description":"","sidebar":"resourcesSidebar"},"resources/applets/intelligibility":{"id":"resources/applets/intelligibility","title":"Intelligibility Builder","description":"Construct a special notation to indicate the intelligibility of a given form in the Slavic languages. Drag and drop a synset XML file to the table below to get started (see an example). After you finish with the table, hover over the code and click the \\"Copy\\" button to copy the code to your clipboard.","sidebar":"resourcesSidebar"},"resources/applets/transliterator":{"id":"resources/applets/transliterator","title":"Transliterator","description":"Converts text from one writing system to another.","sidebar":"resourcesSidebar"},"resources/index":{"id":"resources/index","title":"Zasoby","description":"Witamy w naszym starannie przygotowanym przewodniku, po\u015bwi\u0119conym \u015bwiatu j\u0119zyka mi\u0119dzys\u0142owia\u0144skiego. Mamy szeroki zas\xf3b u\u0142atwiaj\u0105cy Tobie Twoj\u0105 przygod\u0119 z nauk\u0105 \u2014 od spo\u0142eczno\u015bci internetowych i tre\u015bci edukacyjnych do narz\u0119dzi programistycznych i literatury. Wyruszmy wi\u0119c razem na t\u0105 fascynuj\u0105c\u0105 j\u0119zykow\u0105 przygod\u0119.","sidebar":"resourcesSidebar"},"resources/keyboards":{"id":"resources/keyboards","title":"Klawiatury","description":"Windows \\\\","sidebar":"resourcesSidebar"}}}}')}}]);